mapper.select:
SELECT count(*) "count"
  FROM users
  WHERE u_id = #{u_id} AND u_pwd = #{u_pwd}

SELECT *
  FROM users
  WHERE u_id = #{u_id} AND u_pwd = #{u_pwd}

SELECT count(*) "count"
  FROM users
  WHERE u_id = #{u_id} AND u_email = #{u_email} AND u_tell = #{u_tell}

SELECT *
  FROM users
  WHERE u_id = #{u_id} AND u_email = #{u_email} AND u_tell = #{u_tell}

mapper.update:
UPDATE USERS
   SET u_pwd = #{u_pwd}
 WHERE u_id = #{u_id}
